function [fitresult, gof] = createFit(NNEWlocs, NNEWpks)
%CREATEFIT1(NNEWLOCS,NNEWPKS)
%  Create a fit.
%
%  Data for 'untitled fit 1' fit:
%      X Input : NNEWlocs
%      Y Output: NNEWpks
%  Output:
%      fitresult : a fit object representing the fit.
%      gof : structure with goodness-of fit info.
%
%  See also FIT, CFIT, SFIT.

%  Auto-generated by MATLAB on 12-Mar-2015 19:23:32


%% Fit: 'untitled fit 1'.
[xData, yData] = prepareCurveData( NNEWlocs, NNEWpks );

% Set up fittype and options.
ft = fittype( 'smoothingspline' );
opts = fitoptions( 'Method', 'SmoothingSpline' );
opts.SmoothingParam = 0.970521883461111;

% Fit model to data.
[fitresult, gof] = fit( xData, yData, ft, opts );

% Plot fit with data.
% figure( 'Name', 'untitled fit 1' );
hold on;
h = plot( fitresult, xData, yData );
legend( h, 'NNEWpks vs. NNEWlocs', 'untitled fit 1', 'Location', 'NorthEast' );
% Label axes
xlabel( 'NNEWlocs' );
ylabel( 'NNEWpks' );
grid on


